changeSet.sql:
CREATE TABLE role ( id bigint generated by default as identity constraint pk_role primary key, name varchar(255) NOT NULL, description varchar(255) );
CREATE TABLE oauth_user ( id bigint generated by default as identity constraint pk_oauth_user primary key, name varchar(255) NOT NULL, surname varchar(255), email varchar(255), username varchar(255) NOT NULL, enabled boolean default true NOT NULL, password varchar(255) NOT NULL );
CREATE TABLE users_roles ( id bigint generated by default as identity constraint pk_users_roles primary key, user_id integer constraint fk_users_roles_to_user_id references oauth_user, role_id integer constraint fk_users_roles_to_role_id references role );
