changeSet.sql:
    CREATE TABLE oauth2_user (
      id bigint generated by default as identity
           constraint pk_oauth2_user primary key,
      name varchar(255) NOT NULL,
      surname varchar(255),
      email varchar(255) NOT NULL,
      password varchar(255) NOT NULL
  );


CREATE TABLE oauth2_user_role (
     id bigint generated by default as identity
         constraint pk_oauth2_user_role primary key,
     rolename varchar(255) NOT NULL,
     roleDescription varchar(255)
);

CREATE TABLE oauth2_role_permission (
      id bigint generated by default as identity
          constraint pk_oauth2_role_permission primary key,
      permissionName varchar(255) NOT NULL,
      permissionDescription varchar(255)
);
